import { CommonModule, NgOptimizedImage } from '@angular/common';
import {
  ChangeDetectorRef,
  Component,
  DestroyRef,
  HostListener,
  inject,
  Input,
  OnChanges,
  OnInit,
  ViewChild,
} from '@angular/core';
import { RouterModule } from '@angular/router';
import { NzCardModule } from 'ng-zorro-antd/card';
import { NzCarouselComponent, NzCarouselModule } from 'ng-zorro-antd/carousel';
import { NzGridModule } from 'ng-zorro-antd/grid';
import { NzIconModule } from 'ng-zorro-antd/icon';
import { NzPageHeaderModule } from 'ng-zorro-antd/page-header';
import { HomeService } from '../home.service';
import { NzImageModule } from 'ng-zorro-antd/image';

@Component({
  standalone: true,
  selector: 'app-home-brand',
  templateUrl: './home-brand.component.html',
  styleUrls: ['./home-brand.component.scss'],
  imports: [
    CommonModule,
    RouterModule,
    NzGridModule,
    NzCardModule,
    NzIconModule,
    NzCarouselModule,
    NzGridModule,
    NgOptimizedImage,
    NzPageHeaderModule,
    NzImageModule,
  ],
})
export class HomeBrandComponent implements OnInit, OnChanges {
  // props
  fallback =
    'data:image/png;base64,';

  title = 'Top Brands';
  subtitle = '';
  description = '';
  showPrice = false;
  navSpeed = 600;
  categories: any[] = []; // Fill this with your API or static data.
  chunkedCategories: any[][] = [];

  @ViewChild('carousel', { static: false }) carousel!: NzCarouselComponent;

  // data = input<any>;
  @Input data;

  private destroyRef$ = inject(DestroyRef);
  private cd = inject(ChangeDetectorRef);
  private categoryService = inject(HomeService);

  ngOnInit(): void {
    this.cd.detectChanges();
  }

  @HostListener('window:resize')
  onResize() {
    this.chunkCategories();
  }

  chunkCategories(): void {
    const chunkSize = this.getChunkSize();
    this.chunkedCategories = this.chunkArray(this.data, chunkSize);
  }

  getChunkSize() {
    const width = window.innerWidth;
    if (width <= 480) return 4;
    if (width <= 768) return 6;
    return 8;
  }

  chunkArray(array: any[], size): any[][] {
    return Array.from({ length: Math.ceil(array.length / size) }, (_, i) =>
      array.slice(i * size, i * size + size)
    );
  }

  prev(): void {
    this.carousel.pre();
  }

  next(): void {
    this.carousel.next();
  }

  ngOnChanges() {
    this.chunkCategories();
  }
}
